mapper.select:
SELECT a.id id, a.title title, a.address address, a.organizer organizer, a.status status, date_format(a.start_time, '%Y-%m-%d %T') startTime, date_format(a.end_time, '%Y-%m-%d %T') endTime, date_format(a.gmt_create, '%Y-%m-%d %T') gmtCreate, date_format(a.gmt_modified, '%Y-%m-%d %T') gmtModified FROM tb_activity a
a.title LIKE concat('%', #{title}, '%')
AND a.status = #{status}
AND a.deleted = '0'
ORDER BY a.id ASC LIMIT #{offSet}, #{pageRow}
