mapper.select:
SELECT b.id id, b.name name, b.layer layer, b.area area, b.type type, b.description description, b.version version, date_format(b.gmt_create, '%Y-%m-%d %T') gmtCreate, date_format(b.gmt_modified, '%Y-%m-%d %T') gmtModified FROM tb_building b
b.name LIKE concat('%', #{name}, '%')
AND b.type = #{type}
AND b.deleted = '0'
ORDER BY b.id ASC LIMIT #{offSet}, #{pageRow}
