mapper.select:
SELECT c.id id, c.owner_id ownerId, c.owner_name ownerName, c.reason reason, c.description description, c.status status, date_format(c.complaint_Time, '%Y-%m-%d %T') complaintTime, date_format(c.gmt_create, '%Y-%m-%d %T') gmtCreate, date_format(c.gmt_modified, '%Y-%m-%d %T') gmtModified FROM tb_complaint c
c.reason LIKE concat('%', #{reason}, '%')
c.status = #{status}
AND c.deleted = '0'
ORDER BY c.id ASC LIMIT #{offSet}, #{pageRow}
