mapper.select:
SELECT h.id id, h.building_id buildingId, h.building_name buildingName, h.unit_id unitId, h.unit_name unitName, h.code code, h.name name, h.owner_id ownerId, h.owner_name ownerName, h.area area, h.room_num roomNum, h.floor floor, h.telephone telephone, h.status status, h.refined_decoration refinedDecoration, h.description description, date_format(h.live_time, '%Y-%m-%d %T') liveTime, date_format(h.gmt_create, '%Y-%m-%d %T') gmtCreate, date_format(h.gmt_modified, '%Y-%m-%d %T') gmtModified FROM tb_house h
h.code LIKE concat('%', #{code}, '%')
AND h.name LIKE concat('%', #{name}, '%')
AND h.status = #{status}
AND s.deleted = '0'
ORDER BY h.id ASC LIMIT #{offSet}, #{pageRow}
