mapper.select:
SELECT n.id id, n.title title, n.content content, n.status status, date_format(n.release_time, '%Y-%m-%d %T') releaseTime, date_format(n.gmt_create, '%Y-%m-%d %T') gmtCreate, date_format(n.gmt_modified, '%Y-%m-%d %T') gmtModified FROM tb_notice n
n.title LIKE concat('%', #{title}, '%')
AND n.status = #{status}
AND n.deleted = '0'
ORDER BY n.id ASC LIMIT #{offSet}, #{pageRow}
