mapper.select:
SELECT s.id id, s.owner_id ownerId, s.owner_name ownerName, s.title title, s.address address, s.status status, date_format(s.start_time, '%Y-%m-%d %T') startTime, date_format(s.end_time, '%Y-%m-%d %T') endTime, date_format(s.gmt_create, '%Y-%m-%d %T') gmtCreate, date_format(s.gmt_modified, '%Y-%m-%d %T') gmtModified FROM tb_supply s
s.title LIKE concat('%', #{title}, '%')
s.status = #{status}
AND s.deleted = '0'
ORDER BY s.id ASC LIMIT #{offSet}, #{pageRow}
